import React from "react";
import './card.css'
import { useCart,useDispatchCart } from "./ContextReducer";

function Card(props) {
  let option = props.option;
  let optionprice = Object.keys(option);
  let foodItem=props.foodItems;

  return (
    <div
      className="card mt-4 bg-muted border-warning"
      style={{ width: "15rem" }}
    >
      {/* style used in key value pair*/}
      <img
        src={foodItem.img}
        className="card-img-top"
        alt="..."
        style={{ height: "25vh", filter: "brightness(85%)" }}
      />
      <div className="card-body">
        <h5 className="card-title">{foodItem.name}</h5>
        <div className="d-inline-flex inner-card-body">
        <div className="container d-inline-flex pe-0">
          <select
            className="bg-warning custom-select"
            aria-label="Default select example"
          >
            <option selected className="bg-white">
              1
            </option>
            <option className="bg-white" value="1">
              2
            </option>
            <option className="bg-white" value="2">
              3
            </option>
            <option className="bg-white" value="3">
              4
            </option>
          </select>
          <select
            className="bg-warning custom-select "
            aria-label="Default select example"
          >
            {optionprice.map((data) => {
              return (
                <option key={data} value={data}>
                  {data}
                </option>
              );
            })}
          </select>
        </div>
        <div className="h-100">Total Price:</div>
        </div>
        <hr/>
        <button className="btn btn-warning text-black">Add to Cart</button>
      </div>
    </div>
  );
}

export default Card;
