import React, { useState, useEffect } from "react";
import Navbar from "../Navbar";
import Footer from "../Footer";
import "./Home.css";
import Card from "../Card";
import Carousal from "../Carousal";

function Home() {
  const [foodData, setFoodData] = useState([]); //not used for objects
  const [foodCategory, setFoodCategory] = useState([]); //not used for objects

  const loadData = async () => {
    let response = await fetch("http://localhost:5000/api/foodData", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
    });
    response = await response.json();
    setFoodData(response[0]);
    setFoodCategory(response[1]);
  };
  useEffect(() => {
    loadData();
  }, []);

  return (
    <>
      <div>
        <Navbar />
      </div>
      <div>
        <Carousal />
      </div>
      <div className="container d-inline">
        {Array.isArray(foodCategory) && foodCategory.length > 0 ? 
          foodCategory.map((data) => {
            return (
              <div className="row mb-2">
                <div key={data._id} className="fs-5 m-2">
                  {data.CategoryName}
                </div>
                <hr />
                {Array.isArray(foodData) && foodData.length > 0 ? 
                  foodData
                    .filter((items) => items.CategoryName === data.CategoryName)
                    .map((filterItem) => {
                      return (
                        <div
                          key={filterItem._id}
                          className="col-12 col-md-6 col-lg-3"
                        >
                          <Card foodname={filterItem.name}
                          imgsrc={filterItem.img}
                          option={filterItem.option[0]}
                          />
                        </div>
                      );
                    })
                : (
                  <div>""</div>
                )}
              </div>
            );
          })
        : (
          <div>NO SUCH DATA FOUND</div>
        )}
      </div>
      <div>
        <Footer />
      </div>
    </>
  );
}

export default Home;
